using Gpt4All;
using Serilog;
using Serilog.Core;
using Microsoft.Extensions.Logging;

var logger = new LoggerConfiguration()
    .WriteTo.Console()
    .MinimumLevel.Debug()
    .CreateLogger();

var loggerFactory = new LoggerFactory().AddSerilog(logger);

var modelFactory = new Gpt4AllModelFactory(loggerFactory: loggerFactory);

